'use strict';

const
  test = require('tape'),
  dateComparator = require('./index.js');

const
  DATE_2016_07_07 = new Date('2016-07-09'),
  DATE_2016_12_12 = new Date('2016-08-09');

test('dateComparator return value when date1 < date 2', function(t) {
  const
    date1 = DATE_2016_07_07,
    date2 = DATE_2016_12_12,
    expected = -1;

  const actual = dateComparator(date1, date2);

  t.equal(actual, expected, 'dateComparator() should return -1 when date1 < date2');

  t.end();
});

test('dateComparator return value when date1 = date 2', function(t) {
  const
    date1 = DATE_2016_12_12,
    date2 = DATE_2016_12_12,
    expected = 0;

  const actual = dateComparator(date1, date2);

  t.equal(actual, expected, 'dateComparator() should return 0 when date1 = date2');

  t.end();
});

test('dateComparator return value when date1 > date2', function(t) {
  const
    date1 = DATE_2016_12_12,
    date2 = DATE_2016_07_07,
    expected = 1;

  const actual = dateComparator(date1, date2);

  t.equal(actual, expected, 'dateComparator() should return 1 when date1 > date2');

  t.end();
});
