const
  test = require('tape'),
  numberComparator = require('./index.js');

const
  NUM_7 = 7,
  NUM_8 = 8;

test('numberComparator return value when num1 < num2', function(t) {
  const
    num1 = NUM_7,
    num2 = NUM_8,
    expected = -1;

  const actual = numberComparator(num1, num2);

  t.equal(actual, expected, 'numberComparator() should return -1 when num1 < num2');

  t.end();
});

test('numberComparator return value when num1 = num2', function(t) {
  const
    num1 = NUM_8,
    num2 = NUM_8,
    expected = 0;

  const actual = numberComparator(num1, num2);

  t.equal(actual, expected, 'numberComparator() should return 0 when num1 = num2');

  t.end();
});

test('numberComparator return value when num1 > num2', function(t) {
  const
    num1 = NUM_8,
    num2 = NUM_7,
    expected = 1;

  const actual = numberComparator(num1, num2);

  t.equal(actual, expected, 'numberComparator() should return 1 when num1 > num2');

  t.end();
});
