const
  test = require('tape'),
  strDateComparator = require('./index.js');

const
  STR_DATE_2016_07_07 = '2016-07-09',
  STR_DATE_2016_12_12 = '2016-08-09';

test('strDateComparator return value when date1 < date2', function(t) {
  const
    date1 = STR_DATE_2016_07_07,
    date2 = STR_DATE_2016_12_12,
    expected = -1;

  const actual = strDateComparator(date1, date2);

  t.equal(actual, expected, 'strDateComparator() should return -1 when date1 < date2');

  t.end();
});

test('strDateComparator return value when date1 = date2', function(t) {
  const
    date1 = STR_DATE_2016_12_12,
    date2 = STR_DATE_2016_12_12,
    expected = 0;

  const actual = strDateComparator(date1, date2);

  t.equal(actual, expected, 'strDateComparator() should return 0 when date1 = date2');

  t.end();
});

test('strDateComparator return value when date1 > date2', function(t) {
  const
    date1 = STR_DATE_2016_12_12,
    date2 = STR_DATE_2016_07_07,
    expected = 1;

  const actual = strDateComparator(date1, date2);

  t.equal(actual, expected, 'strDateComparator() should return 1 when date1 > date2');

  t.end();
});
