'use strict';

const
  test = require('tape'),
  stringComparator = require('./index.js');

const
  STR_APPLE = 'apple',
  STR_ORANGE = 'Orange';

test('stringComparator return value when str1 < str2', function(t) {
  const
    str1 = STR_APPLE,
    str2 = STR_ORANGE,
    expected = -1;

  const actual = stringComparator(str1, str2);

  t.equal(actual, expected, 'stringComparator() should return -1 when str1 < str2');

  t.end();
});

test('stringComparator return value when str1 = str2', function(t) {
  const
    str1 = STR_ORANGE,
    str2 = STR_ORANGE,
    expected = 0;

  const actual = stringComparator(str1, str2);

  t.equal(actual, expected, 'stringComparator() should return 0 when str1 = str2');

  t.end();
});

test('stringComparator return value when str1 > str2', function(t) {
  const
    str1 = STR_ORANGE,
    str2 = STR_APPLE,
    expected = 1;

  const actual = stringComparator(str1, str2);

  t.equal(actual, expected, 'stringComparator() should return 1 when str1 > str2');

  t.end();
});
